package core_java.operation;

public class FloatDoubleDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int int1 = 1, int2 = 1;
		long long1 = 1, long2 = 1;
		float float1 = 1.0f, float2 = 1.0f;
		double double1 = 1.0, double2 = 1.0;

		int result1 = int1 * int2;
		long result2 = int1 * int2 + long2;
		float result3 = int1 * int2 + long1 + float1;
		double result4 = int1 * int2 + long1 + double2;
	}

}
